                <YouTube 
                    summarizedText = {summarizedText}
                    setSummary = {setSummarizedText}
                    />
            </div>
            <hr className="hr-class"></hr>
            <div style={{ display: 'flex', justifyContent: 'center' }}>
                <Upload 
                    summarizedText = {summarizedText}
                    setSummary = {setSummarizedText}
                    />
            </div>
            <div>
            console.log({summarizedText})
                <Summary />
            </div>
        </div>
    );
}
