import './summary.css';

function Summary(props) {
    return (
        <div className="summary-container">
            <div className='summary-header'>
                Summary
            </div>
            <div className="summary-textbox-class">
                {props.summarizedText ? props.summarizedText : "Your summarized content will appear here once ready."}
            </div>
        </div>
    );
}

export default Summary;
